    const [todo, setTodo] = useState<any[]>([]);
    const handleButtonClick = (value: any) => {

        setTodo([...todo, <p>Todo: {input}</p>]);

    //   setInputValue('');
      };

    return <div>
        <div>I am Todo.</div>
        <input type="text" name="name" value={input} onChange={(e) => setMessage(e.target.value)} />
        <button onClick={() => handleButtonClick(input)}> save</button>
        <p>Message: {input}</p>
        <p>Todo: {todo}</p>
    </div>
}
export default Todo
