const Todo: FC = () => {

    const [input, setMessage] = useState("")
    const [todo, setTodo] = useState<any[]>([]);
    const [countIndex, setCountIndex] = useState<number>(0);

    const handleButtonClick = () => {

        setTodo([...todo, <div>
            {input}<button onClick={() => handleRemoveTodo(countIndex)}>remove</button> 
        </div>]);
        setCountIndex(countIndex + 1);
    };

    const handleRemoveTodo = (indexToRemove: number) => {
        console.log(indexToRemove);

        const updatedTodo = todo.splice(indexToRemove, 1);
        // Update the state with the new array)
        console.log(updatedTodo);

        setTodo(todo);

      };



