import React, { FC, useEffect, useState } from 'react'
const Todo: FC = () => {

    const [input, setMessage] = useState("")
    const [todo, setTodo] = useState<any[]>([]);
    const handleButtonClick = () => {

        setTodo([...todo, <div>
            {input}<button onClick={() => handleRemoveTodo(2)}>remove</button> 
        </div>]);

    };

    const handleRemoveTodo = (indexToRemove: number) => {
        // Create a new array without the item at the specified index
        const updatedTodo = todo.splice(indexToRemove, 1);
        // Update the state with the new array)
        console.log(updatedTodo);

        setTodo(todo);
      };



    return <div>
        <div>I am Todo.</div>
        <input type="text" name="name" value={input} onChange={(e) => setMessage(e.target.value)} />
        <button onClick={() => handleButtonClick()}> save</button>
        <p>Todo: {todo}</p>
    </div>
}
export default Todo
