import React, { FC, useEffect, useState } from 'react'
const Todo: FC = () => {

    const [input, setMessage] = useState("")
    const [todo, setTodo] = useState<string[]>([]);
    const [countIndex, setCountIndex] = useState<number>(0);

    const handleButtonClick = () => {

        setTodo([...todo, input]);
        setCountIndex(countIndex + 1);
    };

    const handleRemoveNumber = (indexToRemove: number) => {
        console.log(indexToRemove);

        const updatedTodo = todo.splice(indexToRemove, 1);
        // Update the state with the new array)
        console.log(updatedTodo);

        setTodo(todo);

      };



    return <div>
        <div>I am Todo.</div>
        <input type="text" name="name" value={input} onChange={(e) => setMessage(e.target.value)} />
        <button onClick={() => handleButtonClick()}> save</button>
        <ul>
        {todo.map((number, index) => (
          <li key={index}>
            {number}
            <button onClick={() => handleRemoveNumber(index)}>Remove</button>
          </li>
        ))}
      </ul>
    </div>
}
export default Todo
